class HomeDataModel {
  HomeDataModel({
    this.toolsTitle,
    this.catAlias,
    this.catTitle,
    this.position,
    this.note,
    this.urlAllItemPress,
    this.supGroup,
    this.items,
    this.actionPressAll,
    this.catDescription,
    this.tools,
  });

  HomeDataModel.fromJson(dynamic json) {
    toolsTitle = json['tools_title'];
    catAlias = json['cat_alias'];
    catTitle = json['cat_title'];
    position = json['position'];
    note = json['note'];
    urlAllItemPress = json['urlAllItemPress'];
    if (json['sup_group'] != null) {
      supGroup = [];
      json['sup_group'].forEach((v) {
        supGroup?.add(SupGroupModel.fromJson(v));
      });
    }
    if (json['items'] != null) {
      items = [];
      json['items'].forEach((v) {
        items?.add(HomeItemModel.fromJson(v));
      });
    }
    actionPressAll = json['action_press_all'];
    catDescription = json['cat_description'];
    if (json['tools'] != null) {
      tools = [];
      json['tools'].forEach((v) {
        tools?.add(HomeItemModel.fromJson(v));
      });
    }
  }

  String? toolsTitle;
  String? catAlias;
  String? catTitle;
  String? position;
  String? note;
  String? urlAllItemPress;
  List<SupGroupModel>? supGroup;
  List<HomeItemModel>? items;
  String? actionPressAll;
  String? catDescription;
  List<HomeItemModel>? tools;

  HomeDataModel copyWith({
    String? toolsTitle,
    String? catAlias,
    String? catTitle,
    String? position,
    String? note,
    String? urlAllItemPress,
    List<SupGroupModel>? supGroup,
    List<HomeItemModel>? items,
    String? actionPressAll,
    String? catDescription,
    List<HomeItemModel>? tools,
  }) {
    return HomeDataModel(
      toolsTitle: toolsTitle ?? this.toolsTitle,
      catAlias: catAlias ?? this.catAlias,
      catTitle: catTitle ?? this.catTitle,
      position: position ?? this.position,
      note: note ?? this.note,
      urlAllItemPress: urlAllItemPress ?? this.urlAllItemPress,
      supGroup: supGroup ?? this.supGroup,
      items: items ?? this.items,
      actionPressAll: actionPressAll ?? this.actionPressAll,
      catDescription: catDescription ?? this.catDescription,
      tools: tools ?? this.tools,
    );
  }

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['tools_title'] = toolsTitle;
    map['cat_alias'] = catAlias;
    map['cat_title'] = catTitle;
    map['position'] = position;
    map['note'] = note;
    map['urlAllItemPress'] = urlAllItemPress;
    if (supGroup != null) {
      map['sup_group'] = supGroup?.map((v) => v.toJson()).toList();
    }
    if (items != null) {
      map['items'] = items?.map((v) => v.toJson()).toList();
    }
    map['action_press_all'] = actionPressAll;
    map['cat_description'] = catDescription;
    if (tools != null) {
      map['tools'] = tools?.map((v) => v.toJson()).toList();
    }
    return map;
  }
}
